import React, {useContext} from "react";
import {Link} from "react-router-dom";
import UserContext from "../../context/UserContext";
import Axios from "axios";

export default function Dashboard() {
    const {userData} = useContext(UserContext);

    const onFileChange = (e) => {
        // Get file uploaded by the user
        const file = e.target.files[0];
        if (!file) return;

        const origFileName = file.name;
        const userId = userData.user.id;
        const ext = origFileName.substr(origFileName.lastIndexOf('.') + 1);
        const newFileName = Date.now() + '_' + userId + '.' + ext;
        const token = localStorage.getItem("auth-token");

        const newFile = {
            newFileName,
            userId
        };

        console.log(newFile);
    }

    return (
        <>
            {userData.user ? (
                <div className="page">
                    <h1>Upload File</h1>
                    <input type="file" id="user-upload" onChange={onFileChange} />
                    {/* <Files /> */}
                </div>
            ) : (
                <div className="page">
                    <h2>You are not logged in.</h2>
                    <Link to="/login">Log In</Link>
                </div>
            )}
        </>
    );
}
