import React, {useContext} from "react";
import {Link} from "react-router-dom";
import UserContext from "../../context/UserContext";
import gql from "graphql-tag";
import {useMutation} from "@apollo/react-hooks";
import {filesQuery} from "./Files";
import Axios from "axios";
//import Path from "path";

const uploadFileMutation = gql`
    mutation UploadFile($file: Upload!, $fname: FileName!) {
        uploadFile(file: $file, fname: $fname)
    }
`;

export default function Dashboard() {
    const {userData} = useContext(UserContext);

    const [uploadFile] = useMutation(uploadFileMutation, {
        refetchQueries: [{
            query: filesQuery
        }]
    });

    const onFileChange = (e) => {
        // Get file uploaded by the user
        const file = e.target.files[0];
        if (!file) return;
        const fileName = file.name;
        const userId = userData.user.id;
        const token = localStorage.getItem("auth-token");
        const ext = fileName.substr(fileName.lastIndexOf('.') + 1); 
        const randomFileName = userId + Date.now() + '.' + ext;

        console.log(randomFileName);
        //file.name = randomFileName;


        const newFile = {
            fileName: randomFileName,
            userId
        };

        const headers = {
            'x-auth-token': token
        }

        // console.log(newFile);
        // console.log(headers);

        Axios.post("http://localhost:4000/files/new", newFile, { headers })
             .then((res) => {
                 console.log(res.data);
                 console.log("File stored in the db");


             })
             .catch((error) => {
                 console.error(error)
             });

        console.log("File stored in cloud storage");
        // Call mutation to send file to the server
        uploadFile({
            variables: {
                file,
                fname: randomFileName
            }
        });




    }

    return (
        <>
            {userData.user ? (
                <div className="page">
                    <h1>Upload File</h1>
                    <input type="file" onChange={onFileChange} />
                    {/* <Files /> */}
                </div>
            ) : (
                <div className="page">
                    <h2>You are not logged in.</h2>
                    <Link to="/login">Log In</Link>
                </div>
            )}
        </>
    );
}
