import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import UserContext from '../../context/UserContext';
// import NewlineText from '../misc/NewlineText';

export default function Read(props) {
    const { userData } = useContext(UserContext);
    const [ userUpload, setUserUpload ] = useState({});
    const apiUrl = 'http://localhost:4000/uploads';

    useEffect(() => {
        const userUploadId = props.match.params.userUploadId;
        const token = localStorage.getItem('auth-token');

        const getFileData = () => {
            axios.get(`${apiUrl}/read/${userUploadId}`, {
                headers: {
                    'x-auth-token': token
                }
            }).then((response) => {
                setUserUpload(response.data);
            }).catch(error => console.error(`Error: ${error}`));
        };

        getFileData();
    }, []);

    const trim = (str) => {
        return str.toString()
                  .replace(/^"(.+(?="$))"$/, '$1')
    }

    let detectionArray, detections;
    if (userUpload) detectionArray = userUpload.detections;
    if (detectionArray) detections = detectionArray.map(item => item.text);
    if (detections) console.log(detections);

    return (
        <>
            {userData.user ? (
                <div className="page">
                    <section className="text">
                        {detectionArray && detectionArray.map(item => {
                            <p>
                                {item.text}
                            </p>
                        })}
                    </section>
                </div>
            ) : (
                <div className="page">
                    Access not allowed.
                </div>
            )}
        </>
    )
}
